import { Button, Input, Switch } from 'antd';
import styles from './style.module.scss';
const { TextArea } = Input;

// const defaultAttributes = {
//     myName: '鸡杂不下面的小站',
//     description: '热爱生活，热爱Coding。',
//     smallPic: '',
//     showCountdown: true,
//     showSmallPic: true,
//     graduationYear: 2023
// }

const Article = (props) => {
    const { children = [], changeTempPageChildChildren } = props;
    console.log(props, 'props');
    console.log(children);

    const addItemToChildren = () => {
        const newChildren = [...children];
        newChildren.push({});
        changeTempPageChildChildren(newChildren);
    }

    const DeleteItemFromChildren = (index) => {
        console.log(index);
        const newChildren = [...children];
        newChildren.splice(index, 1);
        changeTempPageChildChildren(newChildren);
    }

    return (
        <div className={styles.ArticleWrapper}>
            <Button type='primary' onClick={addItemToChildren}>新增区块</Button>
            {
                children.map((item, index) => {
                    const { name, content, picUrl, link } = item;
                    return (
                        <div className={styles.area} key={index}>
                            <div className={styles.delete} onClick={() => DeleteItemFromChildren(index)}>X</div>
                            <div className={styles.row}>
                                <span className={styles.label}>区块名字</span>
                                <Input
                                    value={name}
                                    className={styles.content}
                                    placeholder="请输入区块名字"
                                    onChange={(e) => { changeTempPageChildChildren({ name: e.target.value }) }}
                                />
                            </div>
                            <div className={styles.row}>
                                <span className={styles.label}>区块说明</span>
                                <TextArea
                                    value={content}
                                    className={styles.content}
                                    rows={2}
                                    placeholder="请输入区块说明"
                                // onChange={(e) => { changeTempPageChildAttributes({ description: e.target.value }) }}
                                />
                            </div>
                            <div className={styles.row}>
                                <span className={styles.label}>区块图片</span>
                                <Input
                                    value={picUrl}
                                    className={styles.content}
                                    placeholder="请输入区块图片链接"
                                //onChange={(e) => { changeTempPageChildAttributes({ backPic: e.target.value }) }}
                                />
                            </div>
                            <div className={styles.row}>
                                <span className={styles.label}>区块链接</span>
                                <Input
                                    value={link}
                                    className={styles.content}
                                    placeholder="请输入区块链接"
                                //onChange={(e) => { changeTempPageChildAttributes({ graduationYear: e.target.value }) }}
                                />
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}


export default Article;
