import CountDown from '../Countdown';
import styles from './style.module.scss';


const Banner = (props) => {
    const { schema } = props;
    console.log(schema, 111);
    const myName = schema?.attributes?.myName || '鸡杂不下面的小站';
    const description = schema?.attributes?.description || '热爱生活，热爱Coding。';
    const smallPic = schema?.attributes?.smallPic || 'https://blog-project-static-file.oss-cn-hangzhou.aliyuncs.com/images/person.jpg'
    const showSmallPic = schema?.attributes?.showSmallPic;
    const showCountdown = schema?.attributes?.showCountdown;
    const graduationYear = schema?.attributes?.graduationYear;
    return (

        <div className={`wrapper ${styles.BannerWrapper}`}>
            {showCountdown ? <CountDown graduationYear={graduationYear} /> : null}
            <div className={styles.Banner}>
                {showSmallPic ? <img className={styles.PersonImg} src={smallPic} alt='图片'></img> : null}

                <div className={styles.title}>{myName}</div>
                <div className={styles.dsc}>
                    {description}
                </div>
            </div>
        </div>

    )
}

export default Banner;
