import { produce } from 'immer';


const defaultState = {
    schema: {
        name: 'page',
        attributes: {},
        children: []
    }
}

const reducer = (state = defaultState, action) => produce(state, (draft) => {
    switch (action.type) {
        case 'CHANGE_SCHEMA':
            draft.schema = action.value;
            break;
        default:
            break;
    }
});

export default reducer;
